import axios from "axios";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { useContext, useState, useEffect } from "react";
import { AuthContext } from "../context/AuthProvider";
import { HttpHeadersContext } from "../context/HttpHeadersProvider";

function BoardAnswer() {
  const { auth, setAuth } = useContext(AuthContext);
  const { headers, setHeaders } = useContext(HttpHeadersContext);

  const navigate = useNavigate();

  const { parentSeq } = useParams(); // Î∂ÄÎ™® Í∏Ä Î≤àÌò∏

  const location = useLocation();
  const { parentBoard } = location.state;

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const changeTitle = (event) => {
    setTitle(event.target.value);
  };

  const changeContent = (event) => {
    setContent(event.target.value);
  };

  const createBoardAnswer = async () => {
    const request = {
      id: localStorage.getItem("id"),
      title: title,
      content: content,
    };

    await axios
      .post(`http://localhost:8080/board/${parentSeq}/answer`, request, {
        headers: headers,
      })
      .then((response) => {
        console.log("ÎåìÍ∏Ä Îã¨Í∏∞ ÏÑ±Í≥µ.");
        console.log(response.data);

        alert("ÎåìÍ∏Ä Îì±Î°ù ÏôÑÎ£å");
        navigate(`/boardDetail/${response.data.seq}`); // ÏÉàÎ°≠Í≤å Îì±Î°ùÌïú ÎãµÍ∏Ä ÏÉÅÏÑ∏Î°ú Ïù¥Îèô
      })
      .catch((error) => {
        console.log("ÎåìÍ∏Ä Îì±Î°ù ÏóêÎü¨<");
        console.log(error);
      });
  };

  useEffect(() => {
    if (!auth) {
      alert("Î°úÍ∑∏Ïù∏ÏùÑ Ìïú ÏÇ¨Ïö©ÏûêÎßå Í≤åÏãúÍ∏ÄÏóê ÎåÄÌïú ÎãµÍ∏ÄÏùÑ ÏûëÏÑ±Ìï† Ïàò ÏûàÏäµÎãàÎã§.");
      navigate(-1);
    }
  }, []);

  return (
    <div>
      {/* Î∂ÄÎ™® Í≤åÏãúÍ∏Ä Ï†ïÎ≥¥ */}
      <table className="table">
        <tbody>
          <tr>
            <th className="table-primary">ÏûëÏÑ±Ïûê</th>
            <td>
              <input
                type="text"
                className="form-control"
                value={parentBoard.id}
                size="50px"
                readOnly
              />
            </td>
          </tr>

          <tr>
            <th className="table-primary">Ï†úÎ™©</th>
            <td>
              <input
                type="text"
                className="form-control"
                value={parentBoard.title}
                size="50px"
                readOnly
              />
            </td>
          </tr>
        </tbody>
      </table>
      <br />
      <br />

      {/* ÎãµÍ∏Ä ÏûëÏÑ± */}
      <h3>üìå Reply</h3>
      <table className="table">
        <tbody>
          <tr>
            <th className="table-primary">ÏûëÏÑ±Ïûê</th>
            <td>
              <input
                type="text"
                className="form-control"
                value={localStorage.getItem("id")}
                size="50px"
                readOnly
              />
            </td>
          </tr>

          <tr>
            <th className="table-primary">Ï†úÎ™©</th>
            <td>
              <input
                type="text"
                className="form-control"
                value={title}
                onChange={changeTitle}
                size="50px"
              />
            </td>
          </tr>

          <tr>
            <th className="table-primary">ÎÇ¥Ïö©</th>
            <td>
              <textarea
                className="form-control"
                value={content}
                onChange={changeContent}
                rows="10"
              ></textarea>
            </td>
          </tr>
        </tbody>
      </table>

      <div className="my-5 d-flex justify-content-center">
        <button
          className="btn btn-outline-secondary"
          onClick={createBoardAnswer}
        >
          <i className="fas fa-pen"></i> ÎãµÍ∏ÄÎã¨Í∏∞
        </button>
      </div>
    </div>
  );
}

export default BoardAnswer;
