/* Î°úÍ∑∏Ïù∏ Ïª¥Ìè¨ÎÑåÌä∏ */

import axios from "axios";
import { useState, useContext } from "react";
import { useNavigate } from "react-router";
import { AuthContext } from "../context/AuthProvider";
import { HttpHeadersContext } from "../context/HttpHeadersProvider";

function Login() {
  const { auth, setAuth } = useContext(AuthContext);
  const { headers, setHeaders } = useContext(HttpHeadersContext);

  const navigate = useNavigate();

  const [id, setId] = useState("");
  const [pwd, setPwd] = useState("");

  const changeId = (event) => {
    setId(event.target.value);
  };

  const changePwd = (event) => {
    setPwd(event.target.value);
  };

  const login = async () => {
    const request = {
      id: id,
      pwd: pwd,
    };

    await axios
      .post("http://localhost:8080/user/login", request)
      .then((response) => {
        console.log("[Login.js] login() success :D");
        console.log(response.data);

        alert(response.data.id + "Îãò, ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î°úÍ∑∏Ïù∏ ÎêòÏóàÏäµÎãàÎã§ üîê");

        // JWT ÌÜ†ÌÅ∞ Ï†ÄÏû•
        localStorage.setItem("board_access_token", response.data.jwt);
        localStorage.setItem("id", response.data.id);

        setAuth(response.data.id); // ÏÇ¨Ïö©Ïûê Ïù∏Ï¶ù Ï†ïÎ≥¥(ÏïÑÏù¥Îîî Ï†ÄÏû•)
        setHeaders({ Authorization: `Bearer ${response.data.jwt}` }); // Ìó§Îçî Authorization ÌïÑÎìú Ï†ÄÏû•

        navigate("/boardList");
      })
      .catch((err) => {
        console.log("[Login.js] login() error :<");
        console.log(err);

        alert("‚ö†Ô∏è " + err.response.data);
      });
  };

  return (
    <div>
      <table className="table">
        <tbody>
          <tr>
            <th className="col-3">ÏïÑÏù¥Îîî</th>
            <td>
              <input type="text" value={id} onChange={changeId} size="50px" />
            </td>
          </tr>

          <tr>
            <th>ÎπÑÎ∞ÄÎ≤àÌò∏</th>
            <td>
              <input
                type="password"
                value={pwd}
                onChange={changePwd}
                size="50px"
              />
            </td>
          </tr>
        </tbody>
      </table>
      <br />

      <div className="my-1 d-flex justify-content-center">
        <button className="btn btn-outline-secondary" onClick={login}>
          <i className="fas fa-sign-in-alt"></i> Î°úÍ∑∏Ïù∏
        </button>
      </div>
    </div>
  );
}

export default Login;
